mapper.insert:
INSERT INTO announce (
title, content,
file_original, file_system, file_extension
) VALUES (
#{title}, #{content},
#{file_original}, #{file_system}, #{file_extension}
)


  SELECT LAST_INSERT_ID()

mapper.select:
SELECT a.*
from (
select * from announce b
ORDER BY b.idx DESC
) a
limit 0, #{end}
